import os
import base64
import json

# Configuration
images_dir = "images"
base64_data = ""

image_names = [
    "PROOF-OF-IDENTIFICATION.jpg",
    "PROOF-OF-ADDRESS.jpg",
    "ABN-VERIFICATION.jpg",
    "BANK-ACCOUNT-VERIFICATION.jpg"
]

# Create directory
if not os.path.exists(images_dir):
    os.makedirs(images_dir)

# Write images
mapping = {}
for name in image_names:
    path = os.path.join(images_dir, name)
    with open(path, "wb") as f:
        f.write(base64.b64decode(base64_data))
    
    # Add to mapping (with prefix as per task 1.1)
    # Note: The key in the task example was "PROOF-OF-IDENTIFICATION" (no extension)
    key = name.replace(".jpg", "")
    mapping[key] = f"data:image/jpeg;base64,{base64_data}"

# Write JSON
with open("base64_images.json", "w") as f:
    json.dump(mapping, f, indent=2)

print("Assets generated successfully.")
